mapper.select:
SELECT CD_DO, CD_UP, CD_DN, CD_KOR, CD_ENG, CD_DESC, USE_YN, SORT_SEQ, MOD_ID, MOD_DT, CRE_ID, CRE_DT FROM TB_CODE WHERE USE_YN = 'Y'
AND CD_DO = #{cdDo}
AND CD_UP = #{cdUp}
AND CD_DN = #{cdDn}

mapper.update:
UPDATE
	TB_CODE
SET
	USE_YN = 'N',
	MOD_ID = #{modId},
	MOD_DT = SYSDATE()
WHERE
	CD_DO = #{cdDo}
	AND CD_UP = #{cdUp}
	AND CD_DN = #{cdDn}
